<? if(!$news):?><h2>Unable to find the news-post.</h2><? else:?><h2>News</h2><?=$news['title']?> <span class="smaller-text">by: <?=$news['userAcronym']?></span><div class="news"><?=$news['content']?><p class="newsAuthor">Tags:<?foreach(explode(',', $news['tag']) as $tag):?><? if(!empty($tag)): ?><a href="<?=create_url("news/tags/{$tag}");?>"><?=$tag?></a><? endif; ?><? endforeach; ?><br>Category: <a href="<?=create_url("news/category/{$news['category']}")?>"><?=$news['category']?></a><br><span class="smaller-text">@<?=$news['created']?><br><? if($user==$news['userAcronym'] || $admin): ?><?=create_button("news/edit/{$news['id']}", '<img src="'.base_url('site/data/edit.gif').'" /> Edit')?> <? endif; ?></span></p></div><h2>Comments</h2><? foreach($comments as $comment):?><div class="comment"><?=$comment['content']?> <br><br>@<?=$comment['created']?> by <?=$comment['userAcronym']?><? if($user==$comment['userAcronym'] || $admin): ?> <button class="smaller" onclick="javascript:window.location='<?=base_url("news/removecomment/{$comment['id']}")?>';">Remove</button><? endif; ?></div><? endforeach; ?><div class="lineTop"><? if(isset($user)): ?><?=$form?><? else: ?>You must login and be a valid user in order to comment a news-post.<? endif; ?></div><? endif; ?>